  dplyr::arrange(dplyr::desc(Kits), Family, Color, Pattern, Dilution, Spotting, Checkering, Rex, Agouti)

# Create display table with COLORS, PROBABILITY, GENES
display_table <- outcome_summary %>%
  dplyr::mutate(
    COLORS = paste(Family, Color, Pattern, Dilution, Spotting, Checkering, Rex, Agouti, sep = " "),
    PROBABILITY = paste0(Percentage, "%"),
    GENES = paste(
      dplyr::case_when(Agouti == "Agouti" ~ "AA", Agouti == "Solid" ~ "aa", TRUE ~ "Unknown"),
      dplyr::case_when(Color == "Black-based" ~ "BB", Color == "Black (carries chocolate)" ~ "Bb", Color == "Chocolate-based" ~ "bb", TRUE ~ "Unknown"),
      dplyr::case_when(Family == "Full" ~ "CC", Family == "Chinchilla" ~ "cchdcchd", Family == "Seal" ~ "chch", Family == "Sable" ~ "cycy", Family == "Himalayan" ~ "cccc", Family == "Ruby-Eyed White" ~ "cc", TRUE ~ "Unknown"),
      dplyr::case_when(Pattern == "Charlie" ~ "EE", Pattern == "Broken" ~ "Ee", Pattern == "Self" ~ "ee", Pattern == "Harlequin" ~ "Esej", Pattern == "Wideband" ~ "ww", TRUE ~ "Unknown"),
      dplyr::case_when(Dilution == "Normal" ~ "DD", Dilution == "Diluted" ~ "Dd", Dilution == "Blue" ~ "dd", TRUE ~ "Unknown"),
      dplyr::case_when(Spotting == "Solid" ~ "SS", Spotting == "Spotted" ~ "Ss", Spotting == "Heavy spotted" ~ "ss", TRUE ~ "Unknown"),
      dplyr::case_when(Checkering == "Light checkering" ~ "Lc", Checkering == "Medium checkering" ~ "Mc", Checkering == "Heavy checkering" ~ "Hc", TRUE ~ "Unknown"),
      dplyr::case_when(Rex == "Rex" ~ "Rr", Rex == "Normal" ~ "rr", TRUE ~ "Unknown"),
      sep = " "
    )
  ) %>%
  dplyr::select(COLORS, PROBABILITY, GENES)

write.csv(results, "kit_results.csv", row.names = FALSE)

print(display_table, row.names = FALSE)
